import { isAbsoluteUrl, isUrl } from '@vuepress/helper/node';
import { getUrl } from './getUrl.js';
export const getCover = ({ frontmatter }, { options: { base } }, { hostname }) => {
    const { banner, cover } = frontmatter;
    if (banner) {
        if (isAbsoluteUrl(banner))
            return getUrl(hostname, base, banner);
        if (isUrl(banner))
            return banner;
    }
    if (cover) {
        if (isAbsoluteUrl(cover))
            return getUrl(hostname, base, cover);
        if (isUrl(cover))
            return cover;
    }
    return null;
};
